    /**
     * This method creates and updates a new matrix with the minimum distance from an '1' element to nearest '0' element.
     */
    public static int[][] updateMatrix(int[][] mat) {
        int[][] update = new int[mat.length][mat[0].length];
        int distance;
        for (int i = 0; i < update.length; i++) {
            for (int j = 0; j < update[0].length; j++) {
                if (mat[i][j] == 0){
                    update[i][j] = 0;
                } else {
                    LinkedList<Integer> zeros = getCircle(i,j, mat);
                    while (zeros.size()>1){
                        distance = Math.abs(zeros.pollFirst() - i) + Math.abs(zeros.pollFirst() - j);
                        if (update[i][j] > distance) {
                            update[i][j] = distance;
                        } else if (update[i][j] == 0) {
                            update[i][j] = distance;
                        }
                    }
                }
            }
        }
        return update;
    }

    /**
     * The method returns a list containing rows and columns for zeroes from the matrix.
     * It starts searching in a spiral way, starting from a given position.
     * It continues to search, until at least a zero will be found.
     */
    private static LinkedList<Integer> getCircle(int m, int n, int[][] mat){
        int depth = 1;
        int temp;
        boolean flag = true;
        LinkedList<Integer> queue = new LinkedList<>();
        while (flag){
            temp = depth;
            for (int i = Math.min(m+1, mat.length-1); i <= Math.min(m+depth, mat.length-1); i++) {
                for (int j = Math.max(n - temp+1, 0); j <= Math.min(n + temp-1, mat[0].length-1); j++) {
                    if (mat[i][j] == 0) {
                        queue.add(i);
                        queue.add(j);
                        flag = false;
                    }
                }
                temp--;
            }
            temp = depth;
            for (int i = Math.max(m-1, 0); i >= Math.max(m-depth, 0); i--) {
                for (int j = Math.max(n - temp+1, 0); j <= Math.min(n + temp-1, mat[0].length-1); j++) {
                    if (mat[i][j] == 0) {
                        queue.add(i);
                        queue.add(j);
                        flag = false;
                    }
                }
                temp--;
            }
            temp = depth;
            for (int j = Math.max(n - temp, 0); j <= Math.min(n + temp, mat[0].length-1); j++) {
                if (mat[m][j] == 0) {
                    queue.add(m);
                    queue.add(j);
                    flag = false;
                }
            }
            depth++;
        }
        return queue;
    }

    private static int[][] loadMat(String s){
        s = s.replaceAll("\\[\\[", "");
        s = s.replaceAll("]]", "");
        String[] data = s.split("],\\[");
        int[][] mat = new int[data.length][data[0].length()/2+1];
        int i = 0;
        for (String row : data){
            String[] ints = row.split(",");
            for (int j = 0; j < ints.length; j++) {
                mat[i][j] = Integer.parseInt(ints[j]);
            }
            i++;
        }
        return mat;
    }
